function initMap(){
  map =  new google.maps.Map(document.getElementById('vodomap'), {
    center: {lat: 53.9032903, lng: 27.554804},
    zoom: 11,
    disableDefaultUI: true,
    zoomControl: true,
    scrollwheel: false,
    styles: styleMap
  });

  marker = new google.maps.Marker({
    position: {lat: 0, lng: 0},
    map: null,
    icon: '/assets/templates/main/img/map-pin.png'
  });

  geoXml = new geoXML3.parser({
    afterParse: createLayers
  });

  console.log("geoXml: ", geoXml);

  geoXml.parse("/assets/files/Kmz/doc1.kml");

  geoXml = new geoXML3.parser({
    afterParse: createDistricts
  });

  geoXml.parse("/assets/files/Kmz/doc-distr.kml");

  var options = {
    componentRestrictions: {country: 'by'}
  };

  autocomplete = new google.maps.places.Autocomplete($('#address-selector').get(0),options);

  autocomplete.addListener('place_changed', processAddress);
}


function createLayers(data) {
  console.log("data: ", data);
  $.each(data[0].placemarks,function(){
    layers.push({id: this.id, polygon:this.polygon});
  });
}

function createDistricts(data) {
  $.each(data[0].placemarks,function(){
    this.polygon.fillOpacity = 0.5;
    this.polygon.fillColor = '#009EED';
    google.maps.event.clearListeners(this.polygon);
    districts.push({id: this.id,name: this.name,polygon:this.polygon});
  });
}

function processAddress() {
  var place = autocomplete.getPlace(),
      show = false;

  console.log("place: ", place);
  clearLayers();
  marker.setMap(null);

  $.each(layers,function(){
    console.log("this.polygon: ", this.polygon);

    if (google.maps.geometry.poly.containsLocation(place.geometry.location,this.polygon) && (information[this.id] != undefined)) {
      show = false;
      $.each(districts,function(){
        if (google.maps.geometry.poly.containsLocation(place.geometry.location,this.polygon)) {
          var that = this;

          $.each(place.address_components,function(){
            if (this.types[0] == 'street_number') {
              $('#info-vicinity').text(that.name);
              //that.polygon.setMap(map);

              marker.setPosition(place.geometry.location);
              marker.setMap(map);
              map.setCenter(place.geometry.location);
              map.setZoom(12);

              show = true;
            }
          });
        }
      });

      $('#info-vodozbor').html(information[this.id].name);
      $('#info-params-0').html('');
      $('#info-params-1').html('');
      $('#info-params-2').html('');
      $('#info-params-3').html('');
      $('#info-params-4').html('');

      $('#zone_tab1_info').html('');
      $('#zone_tab2_info').html('');
      $('#zone_tab3_info').html('');
      $('#zone_tab4_info').html('');

      var curInfo = information[this.id];

      $('#info-params-1').parent().addClass('hidden');
      $('#info-params-2').parent().addClass('hidden');
      $('#info-params-3').parent().addClass('hidden');
      $('#info-params-4').parent().addClass('hidden');

      $(curInfo.params).each(function(i,val){
        var z = i;
        if (val != null)
          $('#info-params-'+z).parent().removeClass('hidden');
        if (curInfo.titles[z] != undefined)
          $('#zone_tab'+(z)+'_info').html(curInfo.titles[z]);
        $.each(val,function(){
          $('#info-params-'+z).append('<tr>'+
              '<td><span '+(this.help!=''?'data-toggle="popover" data-html="true" data-placement="right" data-trigger="hover" data-content="'+this.help+'"':'')+'>'+this.name+'</span></td>'+
              '<td>'+(this.metric!=undefined?this.metric:'')+'</td>'+
              '<td>'+this.value+'</td>'+
              '<td>'+this.pdk+'</td>'+
              '</tr>');
        });
      });

      for (z = 0; z < 5; z++) {
        if ($('#info-params-'+z+' tr').length > 5) {
          $('#info-params-'+z+' tr:eq(4) ~ tr').hide();
          $('#info-params-'+z+'').append('<tr style="text-align:center;"><td colspan=4><a href="#" data-vsb="0">Показать все</a></td></tr>');

          $('#info-params-'+z+' tr:last-child a').on('click',function(e){
            e.preventDefault();
            var vsb = $(this).data('vsb');
            if (vsb == "0") {
              $(this).parent().parent().parent().find("tr").show();
              $(this).data("vsb","1");
              $(this).text("Свернуть")
            } else {
              $(this).parent().parent().parent().find("tr:eq(4) ~ tr").hide();
              $(this).parent().parent().parent().find("tr:last-child").show();
              $(this).data("vsb","0");
              $(this).text("Показать все");
            }
          });
        }
      }

      $('table [data-toggle="popover"]').popover();
    }
  });

  if (show)
    $('.info-holder').slideDown();
  else {
    $('.info-holder').slideUp();
    $('#address-selector').popover('show');
    $('#address-selector').one("focus",function(){
      $('#address-selector').popover('destroy');
    });
  }
}

function clearLayers() {
  $.each(districts,function(){
    this.polygon.setMap(null);
  });
}
$(function(){
  initMap();
});
